import axios from 'axios';
import './Home.css';
import React, {useEffect, useState} from 'react';
import Board from "../../components/BoardCard/BoardCard";
import {useNavigate} from "react-router-dom";
import {Header} from "../../components/Header/Header";
import CreateNewBoard from "../../components/board/CreateNewBoard";

const BOARD_LIST_API = "http://localhost:8089/api/boards";

function Home() {
    const [boards, setBoards] = useState([])
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();
    const [token, setToken] = useState(localStorage.getItem('JWT'));


    useEffect(() => {

        axios.get(BOARD_LIST_API, {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })
            .then(res => {
                setBoards(res.data);
            })
            .catch(({response: {status}}) => {
                if (status === 401) {
                    navigate("/signin");
                }
            })
            .finally(() => {
                setLoading(false)
            })
    }, [])


    const renderBoards = () => {
        if (loading) {
            return <>
                <button className='buttonForNavigation'><Board loading/></button>
                <button className='buttonForNavigation'><Board loading/></button>
                <button className='buttonForNavigation'><Board loading/></button>
                <button className='buttonForNavigation'><Board loading/></button>
                <button className='buttonForNavigation'><Board loading/></button>
            </>
        }

        return boards.map(board => (
            <button
                className='buttonForNavigation'
                onClick={() => navigate(`/board/${board.id}`)}
                key={board.id}>
                <Board board={board}/>
            </button>
        ))
    }


    return (
        <div className="body-home">
            <Header></Header>
            <div className="section-1">
                <div>
              <span className="icon-boards-title">
                  <img
                      src={'data:image/jpeg;base64,'}
                      className={`image ${loading ? "skeleton img" : ""}`}/>
                  <h3 className={`boards-page-board-section-header-name ${loading ? "skeleton title" : ""}`}>Your boards</h3>
              </span>
                    <div className="renderBoards">
                        {renderBoards()}
                        <div className={`${loading ? "skeleton img" : ""}`}><CreateNewBoard/></div>
                    </div>
                </div>
            </div>

        </div>
    );
}

export default Home;
